package com.junior.curso_java_basico.aula13;

public class OperadoresRelacionais {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor1 = 1;
		int valor2 = 2;
		
		System.out.println("Valor1 == Valor2 = " +(valor1==valor2));
		System.out.println("Valor1 != Valor2 = " +(valor1!=valor2));
		System.out.println("Valor1 > Valor2 = " +(valor1>valor2));
		System.out.println("Valor1 >= Valor2 = " +(valor1>=valor2));
		System.out.println("Valor1 < Valor2 = " +(valor1<valor2));
		System.out.println("Valor1 <= Valor2 = " +(valor1<=valor2));

	}

}
